const axios = require('axios');

//https://fixer.io
const FIXER_API_KEY = '202a46a939c8bfc5fde00e63876739e6';
const FIXER_API = `http://data.fixer.io/api/latest?access_key=${FIXER_API_KEY}`;

//https://restcountries.eu
const REST_COUNTRIES_API = `https://restcountries.eu/rest/v2/currency/{currency}`;

//async/await
//*async functions returns Promises they dont return Values
//async functions provides us a less code and less complex data facility
//*async function is pause by using await keyword


//Fetch data about currencies
const getExchangeData =async (fromCurrency,toCurrency) => {
  try {
    const {data:{rates}}= await axios.get(FIXER_API);
    const euro = 1 / rates[fromCurrency];
    const exchangeRate = euro * rates[toCurrency];

    return exchangeRate;
    
  } catch (error) {
    throw new Error(`Unable to get currency ${fromCurrency} and ${toCurrency}`);
  }

}

getExchangeData('USD','EUR')

//Fetch data about Countries
const getCountries = async (currencyCode) => {
  try {
    const {data} = await (axios.get(`${REST_COUNTRIES_API}/${currencyCode}`));

    return data.map(({name}) => name);
  } catch (error) {
    throw new Error(`Unable to get Countries that use ${currencyCode}`);
  }
}

getCountries('AUD');

const convertCurrency = async (fromCurrency,toCurrency,amount) => {
  fromCurrency = fromCurrency.toUpperCase();
  toCurrency = toCurrency.toUpperCase();

  

  const [countries,exchangeRate] = await Promise.all([
      getCountries(toCurrency),
      getExchangeRate(fromCurrency,toCurrency),
  ]);

  const convertedAmount = (amount * exchangeRate).toFixed(2);

  return `${amount} ${fromCurrency} is worth ${convertedAmount} ${toCurrency}.
  You can Spend theses in following Countries: ${countries}.`
}

convertCurrency('AUD','USD',20)
  .then((result) => console.log(result))
  .catch((error) => console.log(error));
